			async getListMarket() {
				let val = {
					pageNum: 1,
					pageSize: 15
				};
				// 获取指数列表
				let result = await api.getListMarket2(val);
				this.loading = false;
				if (result.status === 0) {
					this.listArr1 = result.data;
				} else {
					this.texts = result.msg;
					this.alertShow = true;
				}
			},
			async getStock() {
				//沪深升序
				let param = {
					asc: 0
				}
				let data = await api.gethsph(param)
				this.loading = false
				if (data.status === 0) {
					this.finished = true
					this.listArr = data.data
				} else {
					this.texts = data.msg
					this.alertShow = true
				}
			},
			async getStocks() {
				//沪深降序
				let param = {
					asc: 1
				}
				let data = await api.gethsph(param)
				this.loadings = false
				if (data.status === 0) {
					this.finisheds = true
					this.listArrs = data.data
				} else {
					this.texts = data.msg
					this.alertShow = true
				}
			},
			goDetail2(item) {
				//   if (this.userData.length == 0) {

				//     return;
				//   }
				var codes = "";
				var names = "";
				var stock_type = "";
				var soks = "";
				var if_zhishu = '0';
				var if_us = '';
				codes = item.symbol.substring(2);
				names = item.name;
				stock_type = item.symbol.substring(0, 2);
				soks = this.filterSH(item.symbol);
				if_zhishu = '0';
				if_us = item.stock_type == 'us' ? '1' : '';
				this.$router.push({
					path: "/kline",
					query: {
						name: names,
						stockplate: item.stock_plate,
						code: codes,
						type: stock_type,
						sok: soks,
						if_us: if_us,
						usType: item.type,
						if_zhishu: if_zhishu,
					}
				});
			},
			goDetail(item) {
				var codes = "";
				var names = "";
				var stock_type = "";
				var soks = "";
				var if_zhishu = '0';
				var if_us = '';
				codes = item.code;
				names = item.name;
				stock_type = item.symbol.substring(0, 2);
				soks = this.filterSH(item.symbol);
				if_zhishu = '0';
				if_us = item.stock_type == 'us' ? '1' : '';
				this.$router.push({
					path: "/kline",
					query: {
						name: names,
						stockplate: item.stock_plate,
						code: codes,
						type: stock_type,
						sok: soks,
						if_us: if_us,
						usType: item.type,
						if_zhishu: if_zhishu,
					}
				});
			},
			goBanKuaiDetail(i) {
				var item = {};
				item.code = i.indexGid;
				item.name = i.indexName;
				item.symbol = i.indexGid;
				this.goDetail(item)
			},
			filterSH(val) {
				if (val.indexOf('sh') >= 0) {
					return 1;
				} else if (val.indexOf('bj') >= 0 || val.indexOf('sz') >= 0) {
					return 0;
				}
			},
			async getBanner() {
				// 获取显示的banner
				let result = await api.getBannerByPlat({
					platType: 'm'
				})
				if (result.status === 0) {
