				// return
				if (data.status == 0) {
					this.artList = data.data.list[0].artCnt;
					this.showNotice = true
				}
			},
			closeNotice(action, done) {
				done(true)
				if (action == 'cancel') {
					this.showNotice = false
					localStorage.removeItem('noticeId');
				} else {
					this.showNotice = false
					localStorage.setItem('noticeId', this.noticeId);
				}
				// done(false)
			},
			async getNotice() {
				// let data = await api.getTopNotice();
				// console.log(data)
				// if (data.data.content) {
				// 	if (localStorage.getItem('noticeId')) {
				// 		if (localStorage.getItem('noticeId') != data.data.id) {
				// 			this.noticeContent = data.data.content
				// 			this.noticeId = data.data.id
				// 			this.showNotice = true
