				// 	// message: data.data.content,
				// 	// }).then(() => {
				// 	// // on close
				// 	// });
				// }
			},
			async getkline(baseTimestamp = Date.now(), basePrice = 5000, dataSize = 800) {
				var opt = {
					code: '000001',
					time: 5,
					ma: 5,
					size: 100,
				}
				let data = await api.getNKLine(opt);
				console.log('getNKLine', data);
				var klinelist = data.data;
				const dataList = []
				for (let i = 0; i < klinelist.length; i++) {
					const element = klinelist[i];
					const kLineModel = {
						open: element.open,
						low: element.low,
						high: element.high,
						close: element.close,
						volume: element.volume,
						timestamp: element.timeStamp
					}
					dataList.push(kLineModel)
				}
				var that = this
				setTimeout(() => {
					this.kLineChart.applyNewData(dataList)
				}, 500);
				if (this.isshow) {
					setTimeout(() => {
						that.getkline()
					}, 3000);
				}

			},
			getsearch() {
				this.$router.push({
					path: '/Searchlist'
				})
			},
