					}
				})
			},
			getHeaderlink(val) {
				if (val == 0) {
					this.$router.push({
						path: '/MyList'
					})
				} else
				if (val == 1) {
					this.$router.push({
						path: '/trading-list'
					})
				} else if (val == 2) {
					this.$router.push({
						// path: '/jijin'
						path: '/about?e=6'
					})

				} else if (val == 3) {
					this.$router.push({
