						path: '/about?e=6'
					})

				} else if (val == 4) {
					this.$router.push({
						path: '/service'
					})
				} else if (val == 5) {
					this.$router.push({
						path: '/openaccount'
					})
				}
			},
			getHeaderlink1(val) {
				switch (val) {
					case 1:
						this.$router.push({
