							path: '/Subscription?idx=5'
						})
						break;
					case 5:
						this.$router.push({
							path: '/Subscription?idx=2'
						})
						break;
					case 6:
						this.$router.push({
							path: '/DragonTiger'
						})
						break;
					case 7:
						this.$router.push({
							path: '/stopRecovery'
						})
						break;
					case 8:
						this.$router.push({
							path: '/topTen'
						})
						break;
					case 9:
						this.$router.push({
							path: '/daylimit'
						})
						break;
					case 10:
						this.$router.push('/about?e=5')
						break;
					case 12:
						this.$router.push('/notify')
						break;
					case 13:
						this.$router.push('/FundingDetails')
						break;
					case 14:
						this.$router.push('/recharge')
						break;
					case 15:
						this.$router.push('/MyList')
						break;
					case 16:
						this.$router.push('/warehouse')
						break;


				}
			},
			async getInfoSite() {
				let data = await api.getInfoSite()
				if (data.status === 0) {
					this.onlineService = data.data.onlineService
				} else {
					this.$store.commit('elAlertShow', {
						'elAlertShow': true,
						'elAlertText': data.msg
					});
				}
			},
		}
	}
